	<!-- SCRIPT PARA ENVIAR MENSAJE Y ALERTAS  -->
	<script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>
    <script>

        function validar_email(email)
        {
            var regla = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            return regla.test(email);

        }
        

        $('body').on('click', '.btn_send', function(){
            event.preventDefault();
             let nombre =  $('#name').val();
			 	 telf    = $('#telf').val();
                 emails   = $('#emails').val();
                 comment  = $('#comment').val();
        
		if(nombre == '' || telf == '' || emails == '' || comment == '')
        {
            Swal.fire({
                        title: "Alert!",
                        text: "Please, fill in the fields!",
                        icon: "warning"
                    });
            return;
        }
         if(!validar_email(emails))
         {
            Swal.fire({
                        title: "Alert!",
                        text: "Wrong email address",
                        icon: "warning"
                    });
            return;
         }
        

        
        $.ajax({
            url     : "{{ route('send_email')}}",
            method  : "POST",
            beforeSend : function(){

                $('.btn_send').prop('disabled', true);
                 $('.btn_send').addClass('d-none');
                $('.btn_reniec_disable').removeClass('d-none');
            },
         
            data    : {nombre: nombre, telf: telf, emails: emails, comment: comment, '_token' : "{{ csrf_token() }}"},
             
            
            success : function(r)
            {
                    if(!r.status)
                    {
                        $('.btn_send').prop('disabled', false);
                        $('.btn_send').removeClass('d-none');
                        $('.btn_reniec_disable').addClass('d-none');

                    }
                    	$('.btn_send').prop('disabled', false);
                        $('.btn_send').removeClass('d-none');
                        $('.btn_reniec_disable').addClass('d-none');

					$('#name').val('');
			 	    $('#telf').val('');
                    $('#emails').val('');
                    $('#comment').val('');

                     Swal.fire({
                        title: "Success!",
                        text: r.message,
                        text: "Your message has been sent",
                        icon: "success"
                    });
                    
            },
            dataType: 'json'
        })

       
        });

		//Te enseñaré uno y luego replicas tu//
		$('body').on('click', '.img1', function(){

			$('#img1').modal('show'); // abrir


		})

    </script>

<!-- SCRIPT PARA MOSTRAR EL TEXTO TRADUCIDO  -->
<script>
        document.getElementById('botonTraduccion').addEventListener('click', function() {
			if (espanol==true){
				espanol=false;
        
			}
			else{
                espanol=true;
            }

            var textoOriginal = document.getElementById('textoOriginal');
            var textoTraduccion = document.getElementById('textoTraduccion');
            var boton = document.getElementById('botonTraduccion');
            
            if (textoTraduccion.classList.contains('hidden')) {
                textoTraduccion.classList.remove('hidden');
                textoOriginal.classList.add('hidden');
                boton.textContent = 'Leer en español';
            } else {
                textoTraduccion.classList.add('hidden');
                textoOriginal.classList.remove('hidden');
                boton.textContent = 'Read in English';
            }
        });
</script>

	

